mapper.select:
        SELECT
        

--         级联表的字段

        FROM chewei  a

        
            
                and a.id in
                
                #{item}
                
            
            
                and a.chewei_name like CONCAT('%',#{params.cheweiName},'%')
            
            
                and a.chewei_types = #{params.cheweiTypes}
            
            
                  and a.chewei_number >= #{params.cheweiNumberStart}   
            
            
                  and a.chewei_number <= #{params.cheweiNumberEnd}   
            
             
                and a.chewei_number = #{params.cheweiNumber}
             
            
                  and a.chewei_jiage >= #{params.cheweiJiageStart}   
            
            
                  and a.chewei_jiage <= #{params.cheweiJiageEnd}   
            
            
                and a.chewei_content like CONCAT('%',#{params.cheweiContent},'%')
            
            
                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 
            
            
                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 
            

        

        order by a.${params.orderBy} desc
