mapper.select:
        SELECT
        

--         级联表的字段
        ,chewei.chewei_name as cheweiName
        ,chewei.chewei_photo as cheweiPhoto
        ,chewei.chewei_types as cheweiTypes
        ,chewei.chewei_number as cheweiNumber
        ,chewei.chewei_jiage as cheweiJiage
        ,chewei.chewei_content as cheweiContent
        ,yonghu.yonghu_name as yonghuName
        ,yonghu.yonghu_photo as yonghuPhoto
        ,yonghu.yonghu_phone as yonghuPhone
        ,yonghu.yonghu_id_number as yonghuIdNumber
        ,yonghu.yonghu_email as yonghuEmail
        ,yonghu.new_money as newMoney
        ,yonghu.yonghu_delete as yonghuDelete

        FROM cheweishenqing  a
        left JOIN chewei chewei ON a.chewei_id = chewei.id
        left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id

        
            
                and a.id in
                
                #{item}
                
            
            
                and (
                    a.chewei_id = #{params.cheweiId}
                )
            
            
                and (
                    a.yonghu_id = #{params.yonghuId}
                )
            
            
                  and a.cheweishenqing_shijian >= #{params.cheweishenqingShijianStart}   
            
            
                  and a.cheweishenqing_shijian <= #{params.cheweishenqingShijianEnd}   
            
             
                and a.cheweishenqing_shijian = #{params.cheweishenqingShijian}
             
            
                and a.cheweishenqing_yesno_types = #{params.cheweishenqingYesnoTypes}
            
            
                and a.cheweishenqing_yesno_text like CONCAT('%',#{params.cheweishenqingYesnoText},'%')
            
            
                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 
            
            
                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 
            

                
            
                and a.chewei_id IS NOT NULL
            
            
                and chewei.chewei_name like CONCAT('%',#{params.cheweiName},'%')
            
            
                and chewei.chewei_types = #{params.cheweiTypes}
            

            
                  and chewei.chewei_number >= #{params.cheweiNumberStart}   
            
            
                  and chewei.chewei_number <= #{params.cheweiNumberEnd}   
            
            
                and chewei.chewei_number = #{params.cheweiNumber}
            
            
                  and chewei.chewei_jiage >= #{params.cheweiJiageStart}   
            
            
                  and chewei.chewei_jiage <= #{params.cheweiJiageEnd}   
            
            
                and chewei.chewei_content like CONCAT('%',#{params.cheweiContent},'%')
            
            
                  and UNIX_TIMESTAMP(chewei.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 
            
            
                  and UNIX_TIMESTAMP(chewei.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 
            
                
            
                and a.yonghu_id IS NOT NULL
            
            
                and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
            
            
                and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
            
            
                and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
            
            
                and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')
            
            
                  and yonghu.new_money >= #{params.newMoneyStart}   
            
            
                  and yonghu.new_money <= #{params.newMoneyEnd}   
            
            
                  and yonghu.yonghu_delete >= #{params.yonghuDeleteStart}   
            
            
                  and yonghu.yonghu_delete <= #{params.yonghuDeleteEnd}   
            
            
                and yonghu.yonghu_delete = #{params.yonghuDelete}
            
        

        order by a.${params.orderBy} desc
